mapper.select:
select
count(*)
from
tb_project
where serviceId = #{serviceId}
and name = #{name}

select id, name, serviceId, eid, address, remark, createUser, createUserPhone, createTime, updateTime from tb_project
1 = 1
and serviceId = #{serviceId}
and (name like concat('%',#{keywords},'%') or createUser like concat('%',#{keywords},'%'))
order by createTime desc
limit #{start},#{size}

select count(*) from ( select tp.id from tb_project tp INNER JOIN tb_project_engineer_mapping tpem ON tpem.projectId = tp.id
1=1
and tpem.engineerId = #{userId}
and tp.serviceId = #{serviceId}
and (tp.name like concat('%',#{keywords},'%') or tp.createUser like concat('%',#{keywords},'%'))
GROUP BY tp.id ) as countNumber

select tp.id, tp.name from tb_project tp INNER JOIN tb_project_engineer_mapping tpem ON tpem.projectId = tp.id
1=1
and tpem.engineerId = #{userId}
and tp.serviceId = #{serviceId}
and (tp.name like concat('%',#{keywords},'%') or tp.createUser like concat('%',#{keywords},'%'))
GROUP BY tp.id order by tp.createTime desc
limit #{start},#{size}
