mapper.select:
select
    count(*)
from
    tb_space_node_info

    1 = 1

        and eid = #{eid}


        and level = #{level}


        and spaceSetId = #{spaceSetId}


        and parentId = #{parentId}


        and name = #{name}

select id, eid, name, parentId, orderNum, spaceSetId, level, isLast, locationName, createTime, updateTime from tb_space_node_info
1 = 1
and eid = #{eid}
and level = #{level}
and parentId = #{parentId}
and spaceSetId = #{spaceSetId}
and name = #{name}
and locationName = #{locationName}
order by orderNum

select
    count(*)
from
    tb_space_node_info

    1 = 1

        and isLast = #{isLast}


        and spaceSetId = #{spaceSetId}


        and eid = #{eid}
