mapper.select:
select count(*) from tb_space_set
1 = 1
and serviceId = #{serviceId}
and eid = #{eid}
and label = #{code}
and (name like concat('%',#{keywords},'%') or phone like concat('%',#{keywords},'%') or eidName like concat('%',#{keywords},'%') or createUser like concat('%',#{keywords},'%'))

select id, eid, name, eidName, address, createUser, phone, label, createTime from tb_space_set
1 = 1
and serviceId = #{serviceId}
and eid = #{eid}
and label = #{code}
and (name like concat('%',#{keywords},'%') or phone like concat('%',#{keywords},'%') or eidName like concat('%',#{keywords},'%') or createUser like concat('%',#{keywords},'%'))
and name = #{name}
order by createTime desc
limit #{start},#{size}

select id, eid, name, eidName, address, createUser, phone, label, createTime from tb_space_set
1 = 1
and serviceId = #{serviceId}
and eid = #{eid}
and label = #{code}
and name = #{keywords}
order by createTime desc
